mapper.insert:

	select employee_seq.nextval from dual

insert into employee values(#{empNo },#{deptNo },#{posDvcd },#{lgnId },#{passwd },#{filePath },#{fileNm },#{empNm }
,#{birthDt },#{gender },#{mobilePhnNo },#{emailAddr },#{hireDt },'N',null,sysdate)

mapper.update:
update employee

	
		dept_no=#{deptNo}
	
	
		, pos_dvcd=#{posDvcd}
	
	
		, passwd=#{passwd}
	
	
		, file_path=#{filePath}
	
	
		, file_nm=#{fileNm}
	
	
		, mobile_phn_no=#{mobilePhnNo}
	
	
		, email_addr=#{emailAddr}
	
	
		, file_path='resign',file_nm='resign',resgn_yn='Y', resgn_dt=sysdate
	

where emp_no=#{empNo}

mapper.select:
select * from employee 

	
		emp_no like #{empNo}
	
	
		and lgn_id like #{lgnId}

select * from (select rownum rn, board.* from (select * from employee

	
		${search} like '%'||#{keyword}||'%'
	
	
		and dept_no=#{deptNo}
	
	and resgn_yn='N'
 
order by emp_no desc) board) where rn between #{startRow} and #{endRow}
